import React, { Component } from 'react'

export class index extends Component {
    constructor(props) {
        super(props)

        this.state = {
             counter: 1
        }
    }

    updateCounter (){
      this.setState({counter: this.state.counter = this.state.counter+1})
    }

    render() {
        return (
            <div>
                <h1>TEST</h1>
                <input type="text" />
                <button onClick={updateCounter}>Click to update counter</button>
                <p>Counter is now: {this.state.counter}</p>
            </div>
        )
    }
}

export default index
